package com.xiaoyu.BaZi.utils;import android.content.Context;import android.view.Gravity;import android.widget.Toast;import com.xiaoyu.BaZi.background.BaZi;public class BaZiToast extends Toast {	private static Toast mToast;	private BaZiToast(Context context) {		super(context);	}	public static Toast makeText(Context context, CharSequence text, int duration) {		if (mToast == null) {			if (context == null) {				mToast = Toast.makeText(BaZi.getInstance().getApplicationContext(), "", duration);			} else {				mToast = Toast.makeText(context, "", duration);			}		}		mToast.setText(text);		mToast.setDuration(duration);		return mToast;	}	public static Toast makeText(Context context, int resId, int duration) {		if (mToast == null) {			if (context == null) {				mToast = Toast.makeText(BaZi.getInstance().getApplicationContext(), "", duration);			} else {				mToast = Toast.makeText(context, "", duration);			}		}		mToast.setText(context.getString(resId));		mToast.setDuration(duration);		return mToast;	}	public static void makeShortText(CharSequence text) {		if (mToast == null) {			mToast = Toast.makeText(BaZi.getInstance().getApplicationContext(), "", Toast.LENGTH_SHORT);		}		mToast.setText(text);		mToast.setDuration(Toast.LENGTH_SHORT);		mToast.show();	}	public static void makeShortTextCenter(CharSequence text) {		if (mToast == null) {			mToast = Toast.makeText(BaZi.getInstance().getApplicationContext(), "", Toast.LENGTH_SHORT);		}		mToast.setText(text);		mToast.setDuration(Toast.LENGTH_SHORT);		mToast.setGravity(Gravity.CENTER, 0, 0);		mToast.show();	}	public static void makeShortText(int resId) {		if (mToast == null) {			mToast = Toast.makeText(BaZi.getInstance().getApplicationContext(), "", Toast.LENGTH_SHORT);		}		mToast.setText(BaZi.getInstance().getApplicationContext().getString(resId));		mToast.setDuration(Toast.LENGTH_SHORT);		mToast.show();	}	public static void makeLongText(CharSequence text) {		if (mToast == null) {			mToast = Toast.makeText(BaZi.getInstance().getApplicationContext(), "", Toast.LENGTH_LONG);		}		mToast.setText(text);		mToast.setDuration(Toast.LENGTH_LONG);		mToast.show();	}	public static void makeLongText(int resId) {		if (mToast == null) {			mToast = Toast.makeText(BaZi.getInstance().getApplicationContext(), "", Toast.LENGTH_LONG);		}		mToast.setText(BaZi.getInstance().getApplicationContext().getString(resId));		mToast.setDuration(Toast.LENGTH_LONG);		mToast.show();	}	public static void showErrorMessage(Request.RequestException e, String title) {		String message;		if (e.getResponse() == null) {			message = "服务器繁忙~";		} else {			message = e.getResponse();		}		if (title.length() == 0) {			title = "操作失败~";		}		makeShortText(title + "," + message);	}}